const getPage=(t,e)=>{document.getElementById("root")&&t[e||window.location.pathname]()};export const router=t=>{const{routes:e}=t;window.addEventListener("load",(function(){getPage(e)})),window.onpopstate=history.onpushstate=function(t){getPage(e,t.pathname)}};export const navigate=(t,e=null)=>{window.history.pushState(e,null,t)};export const link=(t,e,n)=>{const o=document.createElement("a");o.href=t,o.innerText=e,o.onclick=e=>{e.preventDefault(),navigate(t)},document.getElementById(n).appendChild(o)};